nfp_fp:
theorem is_normal.nfp_fp {f} (H : is_normal f) : ∀ a, f (nfp f a) = nfp f a :=
  @nfp_family_fp unit (fun _ => f) Unit.unit H
#align is_normal.nfp_fp is_normal.nfp_fp

deriv_fp:
theorem is_normal.deriv_fp {f} (H : is_normal f) : ∀ o, f (deriv f o) = deriv f o :=
  @deriv_family_fp unit (fun _ => f) Unit.unit H
#align is_normal.deriv_fp is_normal.deriv_fp
