theorem uniformEmbedding_comap {α : Type _} {β : Type _} {f : α → β} [u : UniformSpace β] (hf : function.injective f) :
    @UniformEmbedding α β (UniformSpace.comap f u) u f :=
  @uniform_embedding.mk _ _ (UniformSpace.comap f u) _ _ (@uniform_inducing.mk _ _ (UniformSpace.comap f u) _ _ rfl) hf
